package davidandroidprojecttools.qq986945193.com.davidandroidprojecttools.utils;import java.util.UUID;/** * @author ：程序员小冰 * @新浪微博 ：http://weibo.com/mcxiaobing * @GitHub: https://github.com/QQ986945193 * @CSDN博客: http://blog.csdn.net/qq_21376985 * @交流Qq ：986945193 * <p/> * 生成32位的随机数 16位数字组成 * <p/> * 类名：由于是随机数，所以，下面的打印结果只是此次的结果 */public class GetUuidRandomUtils {    public static void main(String[] args) {        UUID uuid = UUID.randomUUID();        /**         * .{6c0222ed-e7f5-4cad-a717-a9abfb372239}         */        System.out.println(".{" + uuid.toString() + "}");        /**         * 6c0222ed-e7f5-4cad-a717-a9abfb372239         */        System.out.println(uuid.toString());        /**         * 36         */        System.out.println(uuid.toString().length());        /**         * 32         */        System.out.println(uuid.toString().replace("-", "").length());        /**         * 6c0222ede7f54cada717a9abfb372239         */        System.out.println(uuid.toString().replace("-", ""));    }    /**     * 返回一个随机数     */    public static String getUUIDNum() {        return UUID.randomUUID().toString().replace("-", "").toUpperCase();    }}